import React from "react";
import { Calendar, momentLocalizer } from "react-big-calendar";
import ScheduleDetail from'../components/Schedule-Details';
import moment from "moment";

const events = [    {
  start:  moment().subtract(2, "hours").toDate(),
  end:    moment().toDate(),
  title:  "Meeting about the project"
},];
const localizer = momentLocalizer(moment);  
const employees = ["Emp1", "Emp2"];
function shiftList(props){
  const shifts = props.events;
  const employees = props.employees;
  const employeeDropdown = employees.map((emplyee) =>
    <option value={emplyee}>emplyee</option>
  );
  const shiftsList = shifts.map((shift)  =>
    <li>{shift.title}</li>
  );
  return(
    <ul>{shiftList}</ul>
  );
}

class CreateShift extends React.Component {
    constructor(...args) {
      super(...args)
  
      this.state = { events }
    }
  
    handleSelect = ({ start, end }) => {
      const title = window.prompt('Shift Name')
      if (title)
        this.setState({
          events: [
            ...this.state.events,
            {
              start,
              end,
              title,
            },
          ],
        })
    }

    render() {
        const localizer = momentLocalizer(moment);
        return (
          <>
            <div className="Calendar" id="create_shifts">
                <Calendar
                selectable
                localizer={localizer}
                defaultDate={new Date()}
                
                events={this.state.events}
                onSelectEvent={event=> alert(event.title)}
                onSelectSlot={this.handleSelect}
                defaultView="week"
                />
            </div>
            <div className="AvailalbeShifts">
              <h3>Shifts</h3>
              <shiftList shifts={events} employees={employees} />
            </div>     
            </>       
        );
    }
}

export default CreateShift
